INSERT INTO content_article (id, author_id, author_name, category_id, category_name, title, brief, cover_url, keywords, ip, sort, up_count, down_count, click_count, comment_count, check_state, is_display, is_recommend, gmt_create, gmt_modified) VALUES (1, 1, 'root', 1, '跑步', '這份武夷巖茶的簡介，建議收藏！', '武夷巖茶是產於閩北武夷山巖上烏龍茶類的總稱，主要品種有大紅袍、水仙、奇種、肉桂、名叢等。其中，最為名貴、品質最優的就是大紅袍。', 'http://localhost:28082/tmall-static/resources/article-image/2023/09/05/547736f5-e1e0-4259-9cbd-44b2e40926eb.png', '[{"id":26, "name":"經驗"}]', '127.0.0.1', 99, 0, 0, 0, 0, 1, 1, 1, '2023-09-08 08:00:00', '2023-09-08 08:00:00');
INSERT INTO content_article (id, author_id, author_name, category_id, category_name, title, brief, cover_url, keywords, ip, sort, up_count, down_count, click_count, comment_count, check_state, is_display, is_recommend, gmt_create, gmt_modified) VALUES (2, 1, 'root', 2, '足球', '雲南紅茶有哪些品種', '雲南紅茶品種豐富，有滇紅工夫茶、滇紅金絲、滇紅金針、滇紅金螺、滇紅金芽、野生紅茶、普洱曬紅、紫鵑紅茶、紅碎茶等，其外形各有特定規格，身骨重實，色澤調勻，沖泡後湯色紅鮮明亮，金圈突出，香氣鮮爽，滋味濃強，富有刺激性，葉底紅勻鮮亮，濃、強、鮮為其特色。', 'http://localhost:28082/tmall-static/resources/article-image/2023/09/05/fe24baf0-c029-412a-94d0-bdf6055d7a72.png', '[{"id":26, "name":"經驗"}]', '127.0.0.1', 99, 0, 0, 0, 0, 1, 1, 1, '2023-09-08 08:00:00', '2023-09-08 08:00:00');
INSERT INTO content_article (id, author_id, author_name, category_id, category_name, title, brief, cover_url, keywords, ip, sort, up_count, down_count, click_count, comment_count, check_state, is_display, is_recommend, gmt_create, gmt_modified) VALUES (3, 1, 'root', 3, '籃球', '烏龍茶和普洱茶的區別', '烏龍茶和普洱茶在產地、外形、工藝、滋味等幾個方面均有區別。
INSERT INTO content_article (id, author_id, author_name, category_id, category_name, title, brief, cover_url, keywords, ip, sort, up_count, down_count, click_count, comment_count, check_state, is_display, is_recommend, gmt_create, gmt_modified) VALUES (4, 1, 'root', 1, '經驗', '中國奶茶發源地', '內蒙古是遊牧民族的故鄉，也是奶茶（蒙古奶茶）的最初發源地。蒙古高原和中亞地區的奶茶千百年來從未改變，至今仍然是日常飲用及待客的必備飲料。其他地區則有不同口味的奶茶，如印度奶茶以加入瑪薩拉的特殊香料聞名；發源於香港的絲襪奶茶和發源於台灣的珍珠奶茶也獨具特色。', 'http://localhost:28082/tmall-static/resources/article-image/2023/09/05/ff947d6d-d652-49cd-b000-469c6646cd31.png', '[{"id":26, "name":"經驗"}]', '127.0.0.1', 99, 0, 0, 0, 0, 1, 1, 1, '2023-09-08 08:00:00', '2023-09-08 08:00:00');
INSERT INTO content_article (id, author_id, author_name, category_id, category_name, title, brief, cover_url, keywords, ip, sort, up_count, down_count, click_count, comment_count, check_state, is_display, is_recommend, gmt_create, gmt_modified) VALUES (5, 1, 'root', 1, '經驗', '中國四大毛尖是哪四種', '中國四大毛尖分別為信陽毛尖、都勻毛尖、古丈毛尖、溈山毛尖。
INSERT INTO content_article (id, author_id, author_name, category_id, category_name, title, brief, cover_url, keywords, ip, sort, up_count, down_count, click_count, comment_count, check_state, is_display, is_recommend, gmt_create, gmt_modified) VALUES (6, 1, 'root', 1, '經驗', '357克普洱茶餅尺寸', '以357克普洱茶餅為例，其直徑一般為18~19厘米，周長58.4厘米，重量為357克（凈重），中心厚度2.5厘米，邊緣厚度為1厘米左右，以上參數僅供參考，並非絕對值，因為根據不同的廠家，不同的制作工藝，以及形制需求不同，茶餅的規格都存在一定變化和差異，所以並非定數。', 'http://localhost:28082/tmall-static/resources/article-image/2023/09/05/584a8f58-d6b4-4e7a-885c-7d8f929d2657.png', '[{"id":26, "name":"經驗"}]', '127.0.0.1', 99, 0, 0, 0, 0, 1, 1, 1, '2023-09-08 08:00:00', '2023-09-08 08:00:00');
INSERT INTO content_article (id, author_id, author_name, category_id, category_name, title, brief, cover_url, keywords, ip, sort, up_count, down_count, click_count, comment_count, check_state, is_display, is_recommend, gmt_create, gmt_modified) VALUES (7, 1, 'root', 1, '經驗', '長期收藏白茶，是選擇散茶，還是餅茶', '白茶，有“一年為茶，三年為寶，七年為藥”的說法，因此很多人都喜歡收藏白茶。那收藏散茶好，還是餅茶好呢？', 'http://localhost:28082/tmall-static/resources/article-image/2023/09/05/4c9b6739-948a-4c84-9328-b8ddf21d49eb.png', '[{"id":26, "name":"經驗"}]', '127.0.0.1', 99, 0, 0, 0, 0, 1, 1, 1, '2023-09-08 08:00:00', '2023-09-08 08:00:00');
INSERT INTO content_article (id, author_id, author_name, category_id, category_name, title, brief, cover_url, keywords, ip, sort, up_count, down_count, click_count, comment_count, check_state, is_display, is_recommend, gmt_create, gmt_modified) VALUES (8, 1, 'root', 1, '經驗', '靖安白茶是什麽茶', '靖安白茶是綠茶中的珍稀品種，又稱不發酵茶、白化茶，主產於江西省宜春市靖安縣，其成品茶多為二葉一芯嫩芽，在春季發芽時，新梢嫩葉的葉色呈可逆性白化現象，滿披白毫，外形條素天然，色為玉霜，光亮油潤，肉質自然展開頗具高雅特色。其氨基酸含量是高檔綠茶的6-8倍，倍受人們喜愛。', 'http://localhost:28082/tmall-static/resources/article-image/2023/09/05/0ce4bbb8-f6a2-42d0-b5f8-625118fe4e83.png', '[{"id":26, "name":"經驗"}]', '127.0.0.1', 99, 0, 0, 0, 0, 1, 1, 1, '2023-09-08 08:00:00', '2023-09-08 08:00:00');
INSERT INTO content_article (id, author_id, author_name, category_id, category_name, title, brief, cover_url, keywords, ip, sort, up_count, down_count, click_count, comment_count, check_state, is_display, is_recommend, gmt_create, gmt_modified) VALUES (9, 1, 'root', 1, '經驗', '日月潭紅茶介紹', '日月潭紅茶屬全發酵茶，產自台灣南投縣魚池鄉，由大葉種茶樹鮮葉制作而成，具有形美、香高、味醇等特點。台灣日月潭紅茶，幹茶外形條索粗壯，色澤烏黑油潤，湯色通透明艷，杯壁顯金圈，香氣醇和，帶有蘭花香，滋味甘甜，口感醇厚。', 'http://localhost:28082/tmall-static/resources/article-image/2023/09/05/c0caf9db-4302-4ff6-ae63-ba94c3d21f10.png', '[{"id":26, "name":"經驗"}]', '127.0.0.1', 99, 0, 0, 0, 0, 1, 1, 1, '2023-09-08 08:00:00', '2023-09-08 08:00:00');
INSERT INTO content_article (id, author_id, author_name, category_id, category_name, title, brief, cover_url, keywords, ip, sort, up_count, down_count, click_count, comment_count, check_state, is_display, is_recommend, gmt_create, gmt_modified) VALUES (10, 1, 'root', 1, '經驗', '滿隴紅茶是什麽茶', '滿隴紅茶是浙江紅茶的一種，產自杭州西湖南的山谷，其茶采用當地小葉種為原料，經萎雕、揉撚、發酵、幹燥等工藝制成，成品茶鮮醇甘爽，甜潤濃郁，同時具有形美、香高等特點，其茶條索細長卷曲，通體色澤烏潤，口感甜醇爽滑，滋味甜潤濃郁，兼西湖龍井之清雅，甜香馥郁持久，湯色橙紅明亮，清澈潔凈。', 'http://localhost:28082/tmall-static/resources/article-image/2023/09/05/c12ac3bf-7687-46f5-b97e-3bef794b0a3c.png', '[{"id":26, "name":"經驗"}]', '127.0.0.1', 99, 0, 0, 0, 0, 1, 1, 1, '2023-09-08 08:00:00', '2023-09-08 08:00:00');
INSERT INTO content_article (id, author_id, author_name, category_id, category_name, title, brief, cover_url, keywords, ip, sort, up_count, down_count, click_count, comment_count, check_state, is_display, is_recommend, gmt_create, gmt_modified) VALUES (11, 1, 'root', 1, '經驗', '單叢蜜蘭香介紹', '蜜蘭香是鳳凰單叢十大花蜜香型珍貴名叢之一，是鳳凰水仙群體中的傑出單株，屬烏龍茶系列，產於廣東省潮州市鳳凰鎮，它的母樹距今已經有600多年歷史了，它之所以叫做蜜蘭香，是因為它的成茶具有濃烈的番薯蜜味，其茶蜜香高銳持久，芝蘭香幽雅細長，滋味醇厚回甘，有“濃蜜幽蘭”特韻，飲後滿口生香，極耐沖泡', 'http://localhost:28082/tmall-static/resources/article-image/2023/09/05/c6021d14-91f0-457f-9406-4bf6f7e3a8a3.png', '[{"id":26, "name":"經驗"}]', '127.0.0.1', 99, 0, 0, 0, 0, 1, 1, 1, '2023-09-08 08:00:00', '2023-09-08 08:00:00');
INSERT INTO content_article (id, author_id, author_name, category_id, category_name, title, brief, cover_url, keywords, ip, sort, up_count, down_count, click_count, comment_count, check_state, is_display, is_recommend, gmt_create, gmt_modified) VALUES (12, 1, 'root', 1, '經驗', '家里種植的金絲菊可以嗮幹泡茶嗎', '家里種植的金絲菊不能嗮幹泡茶。菊花茶一般是以鮮嫩菊花花朵作為原料，經過采摘、陰幹、生曬蒸曬以及烘培等工序制作而成的，能沖泡飲用。但自家種的菊花為觀賞菊，不具備食用屬性，雖然進行了曬幹，但是未經過正規工藝加工，且成分尚不明確，因此不能直接泡茶。', 'http://localhost:28082/tmall-static/resources/article-image/2023/09/05/5a9feffd-d02f-495a-a3e7-6c8bdd24a96a.png', '[{"id":26, "name":"經驗"}]', '127.0.0.1', 99, 0, 0, 0, 0, 1, 1, 1, '2023-09-08 08:00:00', '2023-09-08 08:00:00');
