INSERT INTO content_article (id, author_id, author_name, category_id, category_name, title, brief, cover_url, keywords, ip, sort, up_count, down_count, click_count, comment_count, check_state, is_display, is_recommend, gmt_create, gmt_modified) VALUES (22, 1, 'root', 2, '攻效', '雲南紅茶有哪些品種', '雲南紅茶品種豐富，有滇紅工夫茶、滇紅金絲、滇紅金針、滇紅金螺、滇紅金芽、野生紅茶、普洱曬紅、紫鵑紅茶、紅碎茶等，其外形各有特定規格，身骨重實，色澤調勻，沖泡後湯色紅鮮明亮，金圈突出，香氣鮮爽，滋味濃強，富有刺激性，葉底紅勻鮮亮，濃、強、鮮為其特色。', 'http://localhost:28082/tmall-static/resources/article-image/2023/09/05/fe24baf0-c029-412a-94d0-bdf6055d7a72.png', '[{"id":26, "name":"經驗"}]', '127.0.0.1', 99, 0, 0, 0, 0, 1, 1, 0, '2023-09-08 08:00:00', '2023-09-08 08:00:00');
INSERT INTO content_article (id, author_id, author_name, category_id, category_name, title, brief, cover_url, keywords, ip, sort, up_count, down_count, click_count, comment_count, check_state, is_display, is_recommend, gmt_create, gmt_modified) VALUES (23, 1, 'root', 2, '攻效', '這份武夷巖茶的簡介，建議收藏！', '武夷巖茶是產於閩北武夷山巖上烏龍茶類的總稱，主要品種有大紅袍、水仙、奇種、肉桂、名叢等。其中，最為名貴、品質最優的就是大紅袍。', 'http://localhost:28082/tmall-static/resources/article-image/2023/09/05/547736f5-e1e0-4259-9cbd-44b2e40926eb.png', '[{"id":26, "name":"經驗"}]', '127.0.0.1', 99, 0, 0, 0, 0, 1, 1, 0, '2023-09-08 08:00:00', '2023-09-08 08:00:00');
INSERT INTO content_article (id, author_id, author_name, category_id, category_name, title, brief, cover_url, keywords, ip, sort, up_count, down_count, click_count, comment_count, check_state, is_display, is_recommend, gmt_create, gmt_modified) VALUES (24, 1, 'root', 2, '攻效', '單叢蜜蘭香介紹', '蜜蘭香是鳳凰單叢十大花蜜香型珍貴名叢之一，是鳳凰水仙群體中的傑出單株，屬烏龍茶系列，產於廣東省潮州市鳳凰鎮，它的母樹距今已經有600多年歷史了，它之所以叫做蜜蘭香，是因為它的成茶具有濃烈的番薯蜜味，其茶蜜香高銳持久，芝蘭香幽雅細長，滋味醇厚回甘，有“濃蜜幽蘭”特韻，飲後滿口生香，極耐沖泡', 'http://localhost:28082/tmall-static/resources/article-image/2023/09/05/c6021d14-91f0-457f-9406-4bf6f7e3a8a3.png', '[{"id":26, "name":"經驗"}]', '127.0.0.1', 99, 0, 0, 0, 0, 1, 1, 0, '2023-09-08 08:00:00', '2023-09-08 08:00:00');
