INSERT INTO content_article (id, author_id, author_name, category_id, category_name, title, brief, cover_url, keywords, ip, sort, up_count, down_count, click_count, comment_count, check_state, is_display, is_recommend, gmt_create, gmt_modified) VALUES (26, 1, 'root', 3, '存放', '日月潭紅茶介紹', '日月潭紅茶屬全發酵茶，產自台灣南投縣魚池鄉，由大葉種茶樹鮮葉制作而成，具有形美、香高、味醇等特點。台灣日月潭紅茶，幹茶外形條索粗壯，色澤烏黑油潤，湯色通透明艷，杯壁顯金圈，香氣醇和，帶有蘭花香，滋味甘甜，口感醇厚。', 'http://localhost:28082/tmall-static/resources/article-image/2023/09/05/c0caf9db-4302-4ff6-ae63-ba94c3d21f10.png', '[{"id":26, "name":"經驗"}]', '127.0.0.1', 99, 0, 0, 0, 0, 1, 1, 0, '2023-09-08 08:00:00', '2023-09-08 08:00:00');
INSERT INTO content_article (id, author_id, author_name, category_id, category_name, title, brief, cover_url, keywords, ip, sort, up_count, down_count, click_count, comment_count, check_state, is_display, is_recommend, gmt_create, gmt_modified) VALUES (27, 1, 'root', 3, '存放', '滿隴紅茶是什麽茶', '滿隴紅茶是浙江紅茶的一種，產自杭州西湖南的山谷，其茶采用當地小葉種為原料，經萎雕、揉撚、發酵、幹燥等工藝制成，成品茶鮮醇甘爽，甜潤濃郁，同時具有形美、香高等特點，其茶條索細長卷曲，通體色澤烏潤，口感甜醇爽滑，滋味甜潤濃郁，兼西湖龍井之清雅，甜香馥郁持久，湯色橙紅明亮，清澈潔凈。', 'http://localhost:28082/tmall-static/resources/article-image/2023/09/05/c12ac3bf-7687-46f5-b97e-3bef794b0a3c.png', '[{"id":26, "name":"經驗"}]', '127.0.0.1', 99, 0, 0, 0, 0, 1, 1, 0, '2023-09-08 08:00:00', '2023-09-08 08:00:00');
