INSERT INTO content_article (id, author_id, author_name, category_id, category_name, title, brief, cover_url, keywords, ip, sort, up_count, down_count, click_count, comment_count, check_state, is_display, is_recommend, gmt_create, gmt_modified) VALUES (30, 1, 'root', 3, '存放', '家里種植的金絲菊可以嗮幹泡茶嗎', '家里種植的金絲菊不能嗮幹泡茶。菊花茶一般是以鮮嫩菊花花朵作為原料，經過采摘、陰幹、生曬蒸曬以及烘培等工序制作而成的，能沖泡飲用。但自家種的菊花為觀賞菊，不具備食用屬性，雖然進行了曬幹，但是未經過正規工藝加工，且成分尚不明確，因此不能直接泡茶。', 'http://localhost:28082/tmall-static/resources/article-image/2023/09/05/5a9feffd-d02f-495a-a3e7-6c8bdd24a96a.png', '[{"id":26, "name":"經驗"}]', '127.0.0.1', 99, 0, 0, 0, 0, 1, 1, 0, '2023-09-08 08:00:00', '2023-09-08 08:00:00');
