INSERT INTO content_article (id, author_id, author_name, category_id, category_name, title, brief, cover_url, keywords, ip, sort, up_count, down_count, click_count, comment_count, check_state, is_display, is_recommend, gmt_create, gmt_modified) VALUES (32, 1, 'root', 3, '存放', '357克普洱茶餅尺寸', '以357克普洱茶餅為例，其直徑一般為18~19厘米，周長58.4厘米，重量為357克（凈重），中心厚度2.5厘米，邊緣厚度為1厘米左右，以上參數僅供參考，並非絕對值，因為根據不同的廠家，不同的制作工藝，以及形制需求不同，茶餅的規格都存在一定變化和差異，所以並非定數。', 'http://localhost:28082/tmall-static/resources/article-image/2023/09/05/584a8f58-d6b4-4e7a-885c-7d8f929d2657.png', '[{"id":26, "name":"經驗"}]', '127.0.0.1', 99, 0, 0, 0, 0, 1, 1, 0, '2023-09-08 08:00:00', '2023-09-08 08:00:00');
INSERT INTO content_article (id, author_id, author_name, category_id, category_name, title, brief, cover_url, keywords, ip, sort, up_count, down_count, click_count, comment_count, check_state, is_display, is_recommend, gmt_create, gmt_modified) VALUES (33, 1, 'root', 3, '存放', '中國奶茶發源地', '內蒙古是遊牧民族的故鄉，也是奶茶（蒙古奶茶）的最初發源地。蒙古高原和中亞地區的奶茶千百年來從未改變，至今仍然是日常飲用及待客的必備飲料。其他地區則有不同口味的奶茶，如印度奶茶以加入瑪薩拉的特殊香料聞名；發源於香港的絲襪奶茶和發源於台灣的珍珠奶茶也獨具特色。', 'http://localhost:28082/tmall-static/resources/article-image/2023/09/05/ff947d6d-d652-49cd-b000-469c6646cd31.png', '[{"id":26, "name":"經驗"}]', '127.0.0.1', 99, 0, 0, 0, 0, 1, 1, 0, '2023-09-08 08:00:00', '2023-09-08 08:00:00');
