INSERT INTO content_article (id, author_id, author_name, category_id, category_name, title, brief, cover_url, keywords, ip, sort, up_count, down_count, click_count, comment_count, check_state, is_display, is_recommend, gmt_create, gmt_modified) VALUES (59, 1, 'root', 5, '禁忌', '滿隴紅茶是什麽茶', '滿隴紅茶是浙江紅茶的一種，產自杭州西湖南的山谷，其茶采用當地小葉種為原料，經萎雕、揉撚、發酵、幹燥等工藝制成，成品茶鮮醇甘爽，甜潤濃郁，同時具有形美、香高等特點，其茶條索細長卷曲，通體色澤烏潤，口感甜醇爽滑，滋味甜潤濃郁，兼西湖龍井之清雅，甜香馥郁持久，湯色橙紅明亮，清澈潔凈。', 'http://localhost:28082/tmall-static/resources/article-image/2023/09/05/c12ac3bf-7687-46f5-b97e-3bef794b0a3c.png', '[{"id":26, "name":"經驗"}]', '127.0.0.1', 99, 0, 0, 0, 0, 1, 1, 0, '2023-09-08 08:00:00', '2023-09-08 08:00:00');
INSERT INTO content_article (id, author_id, author_name, category_id, category_name, title, brief, cover_url, keywords, ip, sort, up_count, down_count, click_count, comment_count, check_state, is_display, is_recommend, gmt_create, gmt_modified) VALUES (60, 1, 'root', 5, '禁忌', '357克普洱茶餅尺寸', '以357克普洱茶餅為例，其直徑一般為18~19厘米，周長58.4厘米，重量為357克（凈重），中心厚度2.5厘米，邊緣厚度為1厘米左右，以上參數僅供參考，並非絕對值，因為根據不同的廠家，不同的制作工藝，以及形制需求不同，茶餅的規格都存在一定變化和差異，所以並非定數。', 'http://localhost:28082/tmall-static/resources/article-image/2023/09/05/584a8f58-d6b4-4e7a-885c-7d8f929d2657.png', '[{"id":26, "name":"經驗"}]', '127.0.0.1', 99, 0, 0, 0, 0, 1, 1, 0, '2023-09-08 08:00:00', '2023-09-08 08:00:00');
