INSERT INTO content_article (id, author_id, author_name, category_id, category_name, title, brief, cover_url, keywords, ip, sort, up_count, down_count, click_count, comment_count, check_state, is_display, is_recommend, gmt_create, gmt_modified) VALUES (62, 1, 'root', 6, '茶器', '靖安白茶是什麽茶', '靖安白茶是綠茶中的珍稀品種，又稱不發酵茶、白化茶，主產於江西省宜春市靖安縣，其成品茶多為二葉一芯嫩芽，在春季發芽時，新梢嫩葉的葉色呈可逆性白化現象，滿披白毫，外形條素天然，色為玉霜，光亮油潤，肉質自然展開頗具高雅特色。其氨基酸含量是高檔綠茶的6-8倍，倍受人們喜愛。', 'http://localhost:28082/tmall-static/resources/article-image/2023/09/05/0ce4bbb8-f6a2-42d0-b5f8-625118fe4e83.png', '[{"id":26, "name":"經驗"}]', '127.0.0.1', 99, 0, 0, 0, 0, 1, 1, 0, '2023-09-08 08:00:00', '2023-09-08 08:00:00');
