INSERT INTO content_article (id, author_id, author_name, category_id, category_name, title, brief, cover_url, keywords, ip, sort, up_count, down_count, click_count, comment_count, check_state, is_display, is_recommend, gmt_create, gmt_modified) VALUES (65, 1, 'root', 6, '茶器', '這份武夷巖茶的簡介，建議收藏！', '武夷巖茶是產於閩北武夷山巖上烏龍茶類的總稱，主要品種有大紅袍、水仙、奇種、肉桂、名叢等。其中，最為名貴、品質最優的就是大紅袍。', 'http://localhost:28082/tmall-static/resources/article-image/2023/09/05/547736f5-e1e0-4259-9cbd-44b2e40926eb.png', '[{"id":26, "name":"經驗"}]', '127.0.0.1', 99, 0, 0, 0, 0, 1, 1, 0, '2023-09-08 08:00:00', '2023-09-08 08:00:00');
