INSERT INTO content_article (id, author_id, author_name, category_id, category_name, title, brief, cover_url, keywords, ip, sort, up_count, down_count, click_count, comment_count, check_state, is_display, is_recommend, gmt_create, gmt_modified) VALUES (67, 1, 'root', 6, '茶器', '單叢蜜蘭香介紹', '蜜蘭香是鳳凰單叢十大花蜜香型珍貴名叢之一，是鳳凰水仙群體中的傑出單株，屬烏龍茶系列，產於廣東省潮州市鳳凰鎮，它的母樹距今已經有600多年歷史了，它之所以叫做蜜蘭香，是因為它的成茶具有濃烈的番薯蜜味，其茶蜜香高銳持久，芝蘭香幽雅細長，滋味醇厚回甘，有“濃蜜幽蘭”特韻，飲後滿口生香，極耐沖泡', 'http://localhost:28082/tmall-static/resources/article-image/2023/09/05/c6021d14-91f0-457f-9406-4bf6f7e3a8a3.png', '[{"id":26, "name":"經驗"}]', '127.0.0.1', 99, 0, 0, 0, 0, 1, 1, 0, '2023-09-08 08:00:00', '2023-09-08 08:00:00');
INSERT INTO content_article (id, author_id, author_name, category_id, category_name, title, brief, cover_url, keywords, ip, sort, up_count, down_count, click_count, comment_count, check_state, is_display, is_recommend, gmt_create, gmt_modified) VALUES (68, 1, 'root', 6, '茶器', '中國奶茶發源地', '內蒙古是遊牧民族的故鄉，也是奶茶（蒙古奶茶）的最初發源地。蒙古高原和中亞地區的奶茶千百年來從未改變，至今仍然是日常飲用及待客的必備飲料。其他地區則有不同口味的奶茶，如印度奶茶以加入瑪薩拉的特殊香料聞名；發源於香港的絲襪奶茶和發源於台灣的珍珠奶茶也獨具特色。', 'http://localhost:28082/tmall-static/resources/article-image/2023/09/05/ff947d6d-d652-49cd-b000-469c6646cd31.png', '[{"id":26, "name":"經驗"}]', '127.0.0.1', 99, 0, 0, 0, 0, 1, 1, 0, '2023-09-08 08:00:00', '2023-09-08 08:00:00');
INSERT INTO content_article (id, author_id, author_name, category_id, category_name, title, brief, cover_url, keywords, ip, sort, up_count, down_count, click_count, comment_count, check_state, is_display, is_recommend, gmt_create, gmt_modified) VALUES (69, 1, 'root', 6, '茶器', '長期收藏白茶，是選擇散茶，還是餅茶', '白茶，有“一年為茶，三年為寶，七年為藥”的說法，因此很多人都喜歡收藏白茶。那收藏散茶好，還是餅茶好呢？', 'http://localhost:28082/tmall-static/resources/article-image/2023/09/05/4c9b6739-948a-4c84-9328-b8ddf21d49eb.png', '[{"id":26, "name":"經驗"}]', '127.0.0.1', 99, 0, 0, 0, 0, 1, 1, 0, '2023-09-08 08:00:00', '2023-09-08 08:00:00');
INSERT INTO content_article (id, author_id, author_name, category_id, category_name, title, brief, cover_url, keywords, ip, sort, up_count, down_count, click_count, comment_count, check_state, is_display, is_recommend, gmt_create, gmt_modified) VALUES (70, 1, 'root', 6, '茶器', '家里種植的金絲菊可以嗮幹泡茶嗎', '家里種植的金絲菊不能嗮幹泡茶。菊花茶一般是以鮮嫩菊花花朵作為原料，經過采摘、陰幹、生曬蒸曬以及烘培等工序制作而成的，能沖泡飲用。但自家種的菊花為觀賞菊，不具備食用屬性，雖然進行了曬幹，但是未經過正規工藝加工，且成分尚不明確，因此不能直接泡茶。', 'http://localhost:28082/tmall-static/resources/article-image/2023/09/05/5a9feffd-d02f-495a-a3e7-6c8bdd24a96a.png', '[{"id":26, "name":"經驗"}]', '127.0.0.1', 99, 0, 0, 0, 0, 1, 1, 0, '2023-09-08 08:00:00', '2023-09-08 08:00:00');
INSERT INTO content_article (id, author_id, author_name, category_id, category_name, title, brief, cover_url, keywords, ip, sort, up_count, down_count, click_count, comment_count, check_state, is_display, is_recommend, gmt_create, gmt_modified) VALUES (71, 1, 'root', 6, '茶器', '雲南紅茶有哪些品種', '雲南紅茶品種豐富，有滇紅工夫茶、滇紅金絲、滇紅金針、滇紅金螺、滇紅金芽、野生紅茶、普洱曬紅、紫鵑紅茶、紅碎茶等，其外形各有特定規格，身骨重實，色澤調勻，沖泡後湯色紅鮮明亮，金圈突出，香氣鮮爽，滋味濃強，富有刺激性，葉底紅勻鮮亮，濃、強、鮮為其特色。', 'http://localhost:28082/tmall-static/resources/article-image/2023/09/05/fe24baf0-c029-412a-94d0-bdf6055d7a72.png', '[{"id":26, "name":"經驗"}]', '127.0.0.1', 99, 0, 0, 0, 0, 1, 1, 0, '2023-09-08 08:00:00', '2023-09-08 08:00:00');
