INSERT INTO content_article (id, author_id, author_name, category_id, category_name, title, brief, cover_url, keywords, ip, sort, up_count, down_count, click_count, comment_count, check_state, is_display, is_recommend, gmt_create, gmt_modified) VALUES (94, 1, 'root', 8, '花茶', '烏龍茶和普洱茶的區別', '烏龍茶和普洱茶在產地、外形、工藝、滋味等幾個方面均有區別。
INSERT INTO content_article (id, author_id, author_name, category_id, category_name, title, brief, cover_url, keywords, ip, sort, up_count, down_count, click_count, comment_count, check_state, is_display, is_recommend, gmt_create, gmt_modified) VALUES (95, 1, 'root', 8, '花茶', '雲南紅茶有哪些品種', '雲南紅茶品種豐富，有滇紅工夫茶、滇紅金絲、滇紅金針、滇紅金螺、滇紅金芽、野生紅茶、普洱曬紅、紫鵑紅茶、紅碎茶等，其外形各有特定規格，身骨重實，色澤調勻，沖泡後湯色紅鮮明亮，金圈突出，香氣鮮爽，滋味濃強，富有刺激性，葉底紅勻鮮亮，濃、強、鮮為其特色。', 'http://localhost:28082/tmall-static/resources/article-image/2023/09/05/fe24baf0-c029-412a-94d0-bdf6055d7a72.png', '[{"id":26, "name":"經驗"}]', '127.0.0.1', 99, 0, 0, 0, 0, 1, 1, 0, '2023-09-08 08:00:00', '2023-09-08 08:00:00');
INSERT INTO content_article (id, author_id, author_name, category_id, category_name, title, brief, cover_url, keywords, ip, sort, up_count, down_count, click_count, comment_count, check_state, is_display, is_recommend, gmt_create, gmt_modified) VALUES (96, 1, 'root', 8, '花茶', '這份武夷巖茶的簡介，建議收藏！', '武夷巖茶是產於閩北武夷山巖上烏龍茶類的總稱，主要品種有大紅袍、水仙、奇種、肉桂、名叢等。其中，最為名貴、品質最優的就是大紅袍。', 'http://localhost:28082/tmall-static/resources/article-image/2023/09/05/547736f5-e1e0-4259-9cbd-44b2e40926eb.png', '[{"id":26, "name":"經驗"}]', '127.0.0.1', 99, 0, 0, 0, 0, 1, 1, 0, '2023-09-08 08:00:00', '2023-09-08 08:00:00');
